import React, { useState } from 'react';
import axios from 'axios';
import Banner from '../Images/cowin-banner.jpg';

const VaccinationMain = () => {
    const [searchCenter, setSearchCenter] = useState({});

    const getData = async () => {
        const res = await axios.get(`https://cdn-api.co-vin.in/api/v2/appointment/sessions/public/findByPin?pincode=${searchCenter.pincode}&date=${searchCenter.date}`)
    }

    return (<>
        <div>
            <img src={Banner} alt="cowin-banner" className="img-fluid my-5" />
            <div>
                <h2 className="text-center text-white">Know About Your Nearest Vaccination Center</h2>
                <form className="row row-cols-lg-auto g-3 align-items-center">
                    <div className="col-12">
                        <label className="visually-hidden" for="inlineFormInputGroupUsername">Pincode</label>
                        <div className="input-group">
                            <div className="input-group-text">@</div>
                            <input type="Number" className="form-control" id="PinCode" name="PinCode" placeholder="Enter Your PinCode" />
                        </div>
                    </div>

                    <div className="col-12">
                        <label className="visually-hidden" for="inlineFormSelectPref">Preference</label>
                        <select className="form-select" id="inlineFormSelectPref">
                            <option selected>Choose...</option>
                            <option value="1">One</option>
                            <option value="2">Two</option>
                            <option value="3">Three</option>
                        </select>
                    </div>
                </form>
            </div>
        </div>
    </>);
}

export default VaccinationMain;
