import React, { useState } from 'react';
import axios from 'axios';
import RoomIcon from '@material-ui/icons/Room';
import Banner from '../Images/cowin-banner.jpg';

const VaccinationMain = () => {
    const [searchCenter, setSearchCenter] = useState({
        PinCode: "",
    });

    const [ApiData, setApiData] = useState();

    const fetchCenter = (e) => {
        e.preventDefault();
        setApiData(searchCenter);
        getData();
    }

    const userInput = (e) => {
        const { name, value } = e.target;
        setSearchCenter((preVal) => {
            return {
                ...preVal,
                [name]: value
            }
        })
    }

    const date = new Date().toDateString().split(" ");
    const month = new Date().getMonth();
    console.log(date, month);

    const getData = async () => {
        const res = await axios.get(`https://cdn-api.co-vin.in/api/v2/appointment/sessions/public/findByPin?pincode=${ApiData.pincode}&date=${date[2] / month + 1 / date[3]}`);

        console.log(res);
    }

    return (<>
        <div>
            <img src={Banner} alt="cowin-banner" className="img-fluid my-5" />
            <div>
                <h2 className="text-center text-white">Know About Your Nearest Vaccination Center</h2>
                <div className="d-flex justify-content-center align-items-center my-4">
                    <form className="row row-cols-lg-auto g-3 align-items-center" onSubmit={fetchCenter}>
                        <div className="col-12">
                            <label className="visually-hidden" for="inlineFormInputGroupUsername">Pincode</label>
                            <div className="input-group">
                                <div className="input-group-text"><RoomIcon /></div>
                                <input type="Number" className="form-control" id="PinCode" name="PinCode" placeholder="Enter Your PinCode" onChange={userInput} value={searchCenter.pincode} required />
                            </div>
                        </div>
                        <div class="col-12">
                            <button type="submit" class="btn btn-primary">Search</button>
                        </div>
                    </form>
                </div>
            </div>
        </div>
    </>);
}

export default VaccinationMain;
