import React, { useState } from 'react';
import axios from 'axios';
import CalendarTodayIcon from '@material-ui/icons/CalendarToday';
import RoomIcon from '@material-ui/icons/Room';
import Banner from '../Images/cowin-banner.jpg';

const VaccinationMain = () => {
    const [searchCenter, setSearchCenter] = useState({
        pincode: "",
        date: ""
    });

    const userInput = (e) => {
        const [name, value] = e.target;
        console.log()
    }

    const getData = async () => {
        const res = await axios.get(`https://cdn-api.co-vin.in/api/v2/appointment/sessions/public/findByPin?pincode=${searchCenter.pincode}&date=${searchCenter.date}`)
    }

    return (<>
        <div>
            <img src={Banner} alt="cowin-banner" className="img-fluid my-5" />
            <div>
                <h2 className="text-center text-white">Know About Your Nearest Vaccination Center</h2>
                <div className="d-flex justify-content-center align-items-center my-4">
                    <form className="row row-cols-lg-auto g-3 align-items-center">
                        <div className="col-12">
                            <label className="visually-hidden" for="inlineFormInputGroupUsername">Pincode</label>
                            <div className="input-group">
                                <div className="input-group-text"><RoomIcon /></div>
                                <input type="Number" className="form-control" id="PinCode" name="PinCode" placeholder="Enter Your PinCode" onChange="userInput" />
                            </div>
                        </div>
                        <div className="col-12">
                            <label className="visually-hidden" for="inlineFormInputGroupUsername">Pincode</label>
                            <div className="input-group">
                                <div className="input-group-text"><CalendarTodayIcon /></div>
                                <input type="date" className="form-control" id="date" name="date" onChange="userInput" />
                            </div>
                        </div>
                        <div class="col-12">
                            <button type="submit" class="btn btn-primary">Search</button>
                        </div>
                    </form>
                </div>
            </div>
        </div>
    </>);
}

export default VaccinationMain;
